float[] y = new float [3000];

void setup() {
  size(720, 486);
  smooth();
  noStroke();
  for (int i = 0; i < y.length; i++) { 
    y[i] = random(-1000, 200);
  }
}

void draw(){
  
background(#3D46E0);
 for (int i = 0; i < y.length; i++) { 
    y[i] += 0.5; 
     float x = i * 0.4; 
     fill(#F2A200);
     triangle(x, y[i], 10,10,12,12);
 }
}

